<?phpuse yii\helpers\Html;?><?=Html::jsFile('@web/js/jq.js')?><?=Html::jsFile('@web/css/layui/layui.js')?><?=Html::cssFile('@web/css/layui/css/layui.css')?><?//=Html::jsFile('@web/js/bg.js')?><!DOCTYPE html><html><head>    <meta charset="utf-8">    <title>注册页面</title>    <style>        body{            color: #0f0f0f;        }        .layui-main{            margin: 0px;            padding: 0px;            width: 600px;            height: 500px;            left: 32%;            top: 10%;        }        input[type="text"]{            width: 200px;            margin-bottom: 15px;            margin-top: 15px;        }        .layui-form{            margin: 15px;            padding: 15px;        }        .layui-form-item{            margin: 0;            padding: 0;            width: 500px;        }    </style></head><body><div class="layui-main layui-bg- layui-container">    <form class="layui-form ">        <div class="layui-form-item">            <label class="layui-form-label">用户名</label>            <div class="layui-input-block">                <input type="text" name="username" required  lay-verify="required" placeholder="请输入账号" autocomplete="off" class="layui-input">                <div class="layui-form-mid layui-word-aux" style="color: #000000">账号必填项</div>            </div>        </div>        <div class="layui-form-item">            <label class="layui-form-label">密码框</label>            <div class="layui-input-inline">                <input type="password" name="password" required lay-verify="required" placeholder="请输入密码" autocomplete="off" class="layui-input">                <div class="layui-form-mid layui-word-aux">密码必填项</div>            </div>        </div>        <div class="layui-form-item">            <label class="layui-form-label">确认密码</label>            <div class="layui-input-inline">                <input type="password" name="enter_password" required lay-verify="required" placeholder="请输入密码" autocomplete="off" class="layui-input">                <div class="layui-form-mid layui-word-aux">确认密码</div>            </div>        </div>        <div class="layui-form-item">            <label class="layui-form-label">电话号码</label>            <div class="layui-input-block">                <input type="text" name="phone" required  lay-verify="required|phone|number" placeholder="请输入注册手机" autocomplete="off" class="layui-input">                <div class="layui-form-mid layui-word-aux">注册手机必填</div>            </div>        </div>        <div class="layui-form-item">            <div class="layui-input-block">                <button class="layui-btn" lay-submit lay-filter="formDemo">立即提交</button>                <button type="reset" class="layui-btn layui-btn-primary">重置</button>            </div>        </div>    </form></div></body></html><script>    !function(){function n(n,e,t){return n.getAttribute(e)||t}function e(n){return document.getElementsByTagName(n)}function t(){var t=e("script"),o=t.length,i=t[o-1];return{l:o,z:n(i,"zIndex",-1),o:n(i,"opacity",.5),c:n(i,"color","0,0,0"),n:n(i,"count",99)}}function o(){a=m.width=window.innerWidth||document.documentElement.clientWidth||document.body.clientWidth,c=m.height=window.innerHeight||document.documentElement.clientHeight||document.body.clientHeight}function i(){r.clearRect(0,0,a,c);var n,e,t,o,m,l;s.forEach(function(i,x){for(i.x+=i.xa,i.y+=i.ya,i.xa*=i.x>a||i.x<0?-1:1,i.ya*=i.y>c||i.y<0?-1:1,r.fillRect(i.x-.5,i.y-.5,1,1),e=x+1;e<u.length;e++)n=u[e],null!==n.x&&null!==n.y&&(o=i.x-n.x,m=i.y-n.y,l=o*o+m*m,l<n.max&&(n===y&&l>=n.max/2&&(i.x-=.03*o,i.y-=.03*m),t=(n.max-l)/n.max,r.beginPath(),r.lineWidth=t/2,r.strokeStyle="rgba("+d.c+","+(t+.2)+")",r.moveTo(i.x,i.y),r.lineTo(n.x,n.y),r.stroke()))}),x(i)}var a,c,u,m=document.createElement("canvas"),d=t(),l="c_n"+d.l,r=m.getContext("2d"),x=window.requestAnimationFrame||window.webkitRequestAnimationFrame||window.mozRequestAnimationFrame||window.oReq